class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        # this was binary search before. Is it still? 
        # If it works for k, it will still work for k + 1. 
        # Might be BSA
        # less than ten numbers 
        # numbers less than 1000

        # how many queries does it take to reduce index i? 
        # this is like a knapsack. with discrete sum T, it might take O(QT) to see if they work. 
        # might be like O(n^2 logn)

        # Lets focus on the values for a given index.
        # say after processing the queries, we can have values of length Q, (may be zero if idx was not in range)
        # Run standard DP on this. 
        Q = len(queries)
        N = len(nums)
        moves_req = [0] * N
        # get the values offered to each index at each turn 
        idx_to_values = defaultdict(list)
        for l, r, v in queries:
            for idx in range(N):
                if l <= idx <= r:
                    idx_to_values[idx].append(v)
                else:
                    idx_to_values[idx].append(0)




        # now for each idx, determine the min steps to get it to zero.
        # we want the max of these
        for idx in range(N):
            @cache        
            def DP(i, targ):

                """True if we can get target down to zero from index i in the value arr for idx"""
                if targ == 0:
                    #print(f'we can get to zero with {i} queries for {idx}')
                    return i 
                if i == Q:
                    return -1
                if targ < 0:
                    return -1

                val = idx_to_values[idx][i]
                #print(f'using {val} with target {targ}')
                use = DP(i + 1, targ - val)
                lose = DP(i + 1, targ)
                if use >= 0 and lose >= 0:
                    moves_req[idx] = min(use, lose)
                    return min(use, lose)
                if use >= 0:
                    #print(use)
                    moves_req[idx] = use
                    return use
                elif lose>=0:
                    moves_req[idx] = lose
                    return lose
                else:
                    #print(i)
                    moves_req[idx] = -1
                    return -1 
            DP(0, nums[idx])
        M = min(moves_req)
        if M == -1:
            return -1 

        return max(moves_req)

