class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        l, r = 0, len(queries)

        if not self._isAllZero(nums, queries, len(queries)):
            return -1

        res = 0
        while l <= r:
            mid = l + (r-l) // 2
            #print(f'l = {l}, r = {r}, mid = {mid}')
            if not self._isAllZero(nums, queries, mid): 
                l = mid + 1
            else:
                res = mid
                r = mid - 1

        return res

    def _isAllZero(self, nums, queries, k):

        idx_to_decrements = {idx:[] for idx in range(len(nums))}

        for i in range(k):
            l, r, decrement_value = queries[i]
            for j in range(l, r + 1):
                idx_to_decrements[j].append(decrement_value)
        print(idx_to_decrements)
        for idx in idx_to_decrements:
            if not self.isSubsetSum(idx_to_decrements[idx], nums[idx]):
                #print(f'Not possible to sum to {nums[idx]} using {idx_to_decrements[idx]} for idx {idx}')
                return False

        return True

    def isSubsetSum(self, arr, sum):
        n = len(arr)
        prev = [False] * (sum + 1)
        curr = [False] * (sum + 1)

        # Base case: sum 0 can always 
        # be achieved
        prev[0] = True

        # Fill the dp table in a
        # bottom-up manner
        for i in range(1, n + 1):
            for j in range(sum + 1):
                if j < arr[i - 1]:
                    curr[j] = prev[j]
                else:
                    curr[j] = prev[j] or prev[j - arr[i - 1]]
            prev = curr.copy() 

        return prev[sum]

