class Solution {
public:
    int zm(vector<vector<int>>& queries,int ind,int tot,int i, vector<vector<int>> &dp){
        if(tot==0){
            dp[i][tot]=i-1;
            return i-1;
        }
        if(i==queries.size()){
            dp[i][tot]=INT_MAX;
            return INT_MAX;
        }
        if (dp[i][tot] != -1) {
            return dp[i][tot];
        }
        if(dp[i+1][tot]==-1){
            dp[i+1][tot]=zm(queries,ind,tot,i+1,dp);
        }
        int l=dp[i+1][tot];
        if(queries[i][0]<=ind && queries[i][1]>=ind && tot>=queries[i][2]){
            if(dp[i+1][tot-queries[i][2]]==-1){
                dp[i+1][tot-queries[i][2]]=zm(queries,ind,(tot-queries[i][2]),i+1,dp);
            }
            l=min(dp[i+1][tot-queries[i][2]],l);
        }
        dp[i][tot]=l;
        return l;

    }
    int mnz(vector<vector<int>>& queries,int ind,int tot){
        if(tot==0){
            return -1;
        }

        vector<vector<int>> dp(queries.size()+1,vector<int> (tot+1,-1));
        zm(queries,ind,tot,0,dp);
        int l=dp[0][tot];
        // if(l==INT_MAX){
        //     return -1;
        // }
        return l;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans=-1;

        for(int i=0;i<nums.size();i++){

            int l=mnz(queries,i,nums[i]);
            if(l==INT_MAX){
                return -1;
            }
            if(ans<l){
                ans=l;
            }
        }

        return ans+1;
    }
};

