        for(int i=0;i<queries.size();i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                v[j].push_back({queries[i][2],i+1});
            }
        }
        vector<int> maxi(nums.size(),INT_MAX);
        for(int i=0;i<nums.size();i++){
            int targetsum=nums[i];
            if(targetsum==0) {
                maxi[i]=0;
                continue;
            }
            else if(v[i].size()==0) continue;
            else {
                vector<vector<bool>> dp(v[i].size()+1,vector<bool>(targetsum+1,false));
                 vector<vector<int>> index(v[i].size()+1,vector<int>(targetsum+1,INT_MAX));
                for(int j=0;j<=v[i].size();j++) dp[j][0]=true; 
                for(int j=1;j<=v[i].size();j++){
                    for(int k=0;k<=targetsum;k++){
                        dp[j][k]=dp[j-1][k];
                        if(k>=v[i][j-1].first) dp[j][k]=dp[j][k] || dp[j-1][k-v[i][j-1].first];
                         index[j][k] = index[j - 1][k];
                        if(dp[j][k]) index[j][k]=min(index[j][k],v[i][j-1].second);
                    }
                }

                maxi[i]=index[v[i].size()][targetsum];
            }
        }
        int maxim=*max_element(maxi.begin(),maxi.end());
        if(maxim==INT_MAX) return -1;
        else return maxim;
    }
