class Solution {
public:
    int dp[1001][1001];
    bool sol(vector<int>&v,int idx,int sum){
        if(sum==0){
            return true;
        }
        else if(idx==v.size()){
            return false;
        }
        else if(dp[idx][sum]!=-1){
            return dp[idx][sum];
        }
        else
        {

            int ans = false;
            if(v[idx]<=sum){
                ans = sol(v,idx+1,sum-v[idx]);
            }
            return dp[idx][sum] = sol(v,idx+1,sum) || ans;
        }
    }
    bool check(vector<int>&nums,int mid,vector<vector<int>>&q){
        int n=nums.size();
        vector<unordered_map<int,int>>helper(n);
        for(int i=0;i<=mid;i++){
            int start = q[i][0];
            int end = q[i][1]+1;
            int val = q[i][2];
            helper[start][val]++;
            if(end<n){
                helper[end][val]--;
            }
        }
        unordered_map<int,int>m;
        // cout<<"For mid :: "<<mid<<endl;
        for(int i=0;i<n;i++){
            for(auto &it:helper[i]){
                m[it.first]+=it.second;
            }
            vector<int>v;
            for(auto &it:m){
                int times = it.second;
                while(times--)
                v.push_back(it.first);
            }

            memset(dp,-1,sizeof(dp));
            if(!sol(v,0,nums[i])){
                return false;
            }

        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int low =0 ;
        int n=q.size();
        int high = n-1;
        int ans = -1;
        int count=0;
        for(auto &i:nums){
            if(i==0){
                count++;
            }
        }
        if(count==nums.size()){
            return 0;
        }
        while(low<=high){
            int mid = (low+high)/2;
            if(check(nums,mid,q)){
                ans = mid+1;
                high = mid -1;
            }
            else{
                low = mid+1;
            }
        }
        return ans;
    }
};

