class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        boolean[][] test = new boolean[n][1001];
        int zz=0;
        while(zz<nums.length){
            test[zz][0] = true;
            zz++;
        }
        boolean zero=true;
        for(int i:nums)
            {
                if(i!=0) zero=false;
            }
        if (zero) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];

            for (int j = l; j <= r; j++) {
                boolean[] shift = new boolean[1001];

                for (int i = 0; i < 1001 - v; i++) {
                    if (test[j][i]) {
                        shift[i + v] = true;
                    }
                }

                for (int i = 0; i < 1001; i++) {
                    test[j][i] |= shift[i];
                }

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    test[j][pos] = false;
                }
            }

            boolean achieved = true;
            int tt = 0;
            while (tt < n) {
                if (!test[tt][nums[tt]]) {
                    achieved = false;
                    break;
                }
                tt++;
                }

            if (achieved) return k + 1;
        }
        return -1;
    }
}

