class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        dp = [[False] * (num + 1) for num in nums]
        n  = len(nums)
        m  = len(queries)
        res = 0
        min_val = [0] * n
        for a in range(n):
            dp[a][nums[a]] = True
            min_val[a] = nums[a]

        if (all(row[0] for row in dp)):
            return res

        for b in range(m):
            l = queries[b][0]
            r = queries[b][1]
            val = queries[b][2]
            for i in range(l, r + 1):
                for j in range(val, len(dp[i])):
                    if dp[i][j] == True:
                        dp[i][j - val] = True
                        min_val[i] = min(min_val[i], j - val)
            res = res + 1
            if (all(row[0] for row in dp)):
                return res

        return -1

