class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int ans = 0;
        int n = nums.size();
        // int zeros = 0;
        for(int i=0;i<n;i++){
            int ind = -1;
            int tar = nums[i];
            if(tar == 0){
                continue;
            }
            set<int> st;
            for(int j=0;j<q;j++){
                int l = queries[j][0];
                int r = queries[j][1];
                int val = queries[j][2];
                if(i>=l && i<=r){
                    vector<int> tmp = {val};
                    for(int it: st){
                        tmp.push_back(it+val);
                    }
                    for(int it: tmp){
                        st.insert(it);
                    }
                    if(st.find(tar) != st.end()){
                        ind = j+1;
                        break;
                    }
                }
            }
            if(ind == -1) return -1;
            ans = max(ans, ind);
        }

        return ans;      
    }
};

