class Solution {
public:
    int Memo(vector<vector<int>>& q,int i,int Sum,int& idx,vector<vector<int>>& dp){
        if(Sum==0) return i;
        if(Sum<0 || i>=q.size()) return INT_MAX;
        if(dp[i][Sum]!=-1) return dp[i][Sum];
        int res = INT_MAX;
        if(idx>=q[i][0] && idx<=q[i][1]){
            res = Memo(q,i+1,Sum-q[i][2],idx,dp);
        }
        res = min(res,Memo(q,i+1,Sum,idx,dp));
        return dp[i][Sum] = res;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        for(int i=0;i<nums.size();i++){
            if(nums[i]==0) continue;
            vector<vector<int>> dp(queries.size(),vector<int>(1001,-1));
            auto ans = Memo(queries,0,nums[i],i,dp);
            res = max(ans,res);
        }
        return (res==INT_MAX)?-1:res;
    }
};

