class Solution {
public:
    bool canCreateZero(const vector<int>& nums,
                       const vector<vector<int>>& queries, int k) {
        int n = (int)nums.size();

        for (int i = 0; i < n; i++) {
            int target = nums[i];

            vector<int> vals;
            for (int q = 0; q < k; q++) {

                if ((int)queries[q].size() < 3)
                    continue;
                int l = queries[q][0], r = queries[q][1], val = queries[q][2];

                if (l < 0)
                    l = 0;
                if (r >= n)
                    r = n - 1;
                if (i >= l && i <= r) {
                    vals.push_back(val);
                }
            }

            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int v : vals) {

                for (int s = target; s >= v; s--) {
                    if (dp[s - v]) {
                        dp[s] = true;
                    }
                }
            }
            if (!dp[target]) {

                return false;
            }
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = (int)queries.size();

        for (int k = 0; k <= m; k++) {
            if (canCreateZero(nums, queries, k)) {
                return k;
            }
        }
        return -1;
    }
};

