using namespace std;

class Solution {
public:
    int rec(int k, vector<bitset<1001>> dp, const vector<int>& nums, const vector<vector<int>>& queries) {
        int n = nums.size();
        if (k == queries.size()) return -1;
        int l = queries[k][0], r = queries[k][1], v = queries[k][2];
        for (int j = l; j <= r; j++) {
            dp[j] |= (dp[j] << v);
            for (int pos = nums[j] + 1; pos < 1001; pos++) {
                dp[j].reset(pos);
            }
        }
        bool check = true;
        for (int j = 0; j < n; j++) {
            if (!dp[j].test(nums[j])) {
                check = false;
                break;
            }
        }
        if (check) return k + 1;
        return rec(k + 1, dp, nums, queries);
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<bitset<1001>> dp(n);
        for (int j = 0; j < n; j++) {
            dp[j].set(0);
        }
        bool check = true;
        for (int j = 0; j < n; j++) {
            if (nums[j] != 0) {
                check = false;
                break;
            }
        }
        if (check) return 0;
        return rec(0, dp, nums, queries);
    }
};

