class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if max(nums)==0: return 0

        def possible(q:int)-> bool:
            for i,num in enumerate(nums):
                qs = []
                for qu in queries[:q+1]:
                    if qu[0]<=i<=qu[1]:
                        qs.append(qu[2])

                @cache
                def valid(ind:int,remaining:int) -> bool:
                    if remaining ==0: return True
                    elif remaining<0: return False
                    elif ind<0: return False
                    return valid(ind-1,remaining) or valid(ind-1,remaining-qs[ind])
                if not valid(len(qs)-1,num): return False

            return True

        low,high = 0,len(queries)
        while low<high:
            mid = (low+high)//2
            if possible(mid):
                high = mid
            else:
                low = mid+1
        return low+1 if low+1<=len(queries) else -1

