def helper(nums, queries):
    if all(x==0 for x in nums): return 0
    dp = [[False]*1001 for _ in nums]
    for row in dp: row[0] = True
    for i, (l, r, v) in enumerate(queries):
        for j in range(l, r+1):
            shifted = [False]*1001
            for s in range(1001 - v):
                if dp[j][s]: shifted[s+v] = True
            for s in range(1001):
                dp[j][s] |= shifted[s]
            for s in range(nums[j]+1, 1001):
                dp[j][s] = False
        if all(dp[idx][nums[idx]] for idx in range(len(nums))):
            return i+1
    return -1

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        return helper(nums,queries)

