class Solution {
     public int minIndexForSubsetSum(List<Integer> nums, int k) {
        int n = nums.size();
        int[][] dp = new int[n + 1][k + 1]; 
        for (int[] row : dp) Arrays.fill(row, Integer.MAX_VALUE); 
        dp[0][0] = -1; // Base case: sum 0 at index -1

        for (int i = 0; i < n; i++) {
            int num = nums.get(i);
            for (int sum = 0; sum <= k; sum++) {
                if (dp[i][sum] != Integer.MAX_VALUE) {
                    dp[i + 1][sum] = Math.min(dp[i + 1][sum], dp[i][sum]); // Exclude current element
                    if (sum + num <= k) {
                        dp[i + 1][sum + num] = Math.min(dp[i + 1][sum + num], i); // Include current element
                    }
                }
            }
        }

        return dp[n][k] == Integer.MAX_VALUE ? -1 : dp[n][k]; // Minimum index where subset sum == k
    }
    public int minZeroArray(int[] nums, int[][] qr) {
        int n = nums.length;
         List<Integer>[][] list = new ArrayList[n][2]; // Correct way to declare
        for (int i = 0; i < n; i++) {
            list[i][0] = new ArrayList<>();
            list[i][1] = new ArrayList<>();
        }
        int k = 1;
        for( int[] q : qr){
            int l =q[0];
            int r= q[1];
            int val = q[2];
            for( int i = l ; i<= r ; i++){
                list[i][0].add(val);
                list[i][1].add(k);
            }
            k++;
        }
        int ans = 0;
        for( int i =0; i<n;i++){
            if(nums[i] ==0){
                continue;
            }
            int ele = minIndexForSubsetSum(list[i][0], nums[i]);
            if(ele==-1) return -1;
            ans = Math.max(ans,list[i][1].get(ele));
        }
        return ans;
    }
}

