    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int height = nums.size() ; 
        int greater= 0  ;  
        bool choice = true ; 
        for(int fc=0;fc<height;fc++){
            std::vector<bool> dynamics(RANGE+1) ; 
            dynamics[0] = true ;  
            if(dynamics[nums[fc]]==true){
                greater = fmax(0,greater) ; 
                continue ; 
                }  
            for(int fd=0;fd<queries.size();fd++){
                std::vector<int>& phase = queries[fd] ; 
                if(fc<=phase[1]&&fc>=phase[0]){
                    for(int fe=RANGE;fe>=0;fe--){
                        if(dynamics[fe]==true){
                            if(fe+phase[2]<=RANGE){
                                dynamics[fe+phase[2]] = true ;  
                            }
                        }
                    }
                }  
                if(dynamics[nums[fc]]==true){
                    greater = fmax(fd+1,greater) ; 
                    break ; 
                }
            }  
            if(dynamics[nums[fc]]==false){
                choice = false ; 
                break  ;  
            }
        }  
        if(choice==false){return -1 ; }
        return  greater ;  
    }
};

