class Bag:
    num: int
    flag: bool = False
    memo: set
    queries: int = 0

    def __init__(self, num):
        if num == 0:
            self.flag = True
            Bag.queries -= 1
            return
        self.num = num
        self.memo = {num}

    def add(self, num):
        if self.flag:
            return
        new_memo = set()
        for x in self.memo:
            if x == num:
                self.flag = True
                Bag.queries -= 1
                return
            elif x - num > 0:
                new_memo.add(x - num)
        self.memo |= new_memo


class Solution:
    def minZeroArray(self, nums: list[int], queries: list[list[int]]) -> int:
        m = len(nums)
        n = len(queries)
        Bag.queries = m
        table = [Bag(nums[i]) for i in range(m)]
        if Bag.queries == 0:
            return 0
        for i in range(n):
            l, r, val = queries[i]
            for j in range(l, r + 1):
                table[j].add(val)
                if Bag.queries == 0:
                    return i + 1
        return -1

