class Solution {
public:



    int f(int sum, int idx, vector<int> &op, map<pair<int,int>, int>& dp){
        if(sum == 0){
            return min(idx, (int)op.size());
        }
        if(idx >= op.size()){
            return -1;
        }
        pair<int,int> p = make_pair(sum,idx);
        if(dp.find(p) != dp.end()){
            return dp[p];
        }

        int take = -1, dont = f(sum, idx+1, op,dp);
        if(sum >= op[idx]){
            take = f(sum-op[idx], idx+1, op,dp);
        }

        int here = dont;
        if(take != -1){
            if(here == -1){
                here =take;
            }
            here = min(here, take);
        }

        dp[p] = here;
        return here;
    }

    int solveFor(vector<int> & op,vector<int> & step, int sum){
        if(sum == 0){return 0;}
        map<pair<int,int>, int> dp;

        int here = f(sum, 0, op,dp);


        if(here == -1){
            return here;
        }
        if(here == 0){
            return 0;
        }

        return step[here-1];
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& qs) {
        int n = nums.size(), q = qs.size();
        vector<int> ops[n];
        int s =1;
        vector<int> step[n];
        for(vector<int> &q : qs){
            int l = q[0] , r = q[1], v = q[2];

            for(int i = l;i<=r;i++){
                ops[i].push_back(v);
                step[i].push_back(s);
            }

            s++;

        }

        int ans = -1, notfound = 0;
        for(int i =0;i<n;i++){
           int here = solveFor(ops[i],step[i], nums[i]);
            if(here == -1){
                return -1;
            }
            ans = max(ans, here);
        }

        return ans;




    }
};

