#define inf 100000000
class Solution {
public:
    int fun(vector<pair<int,int>>&vec, int val, int ind, vector<vector<int>>&dp){
        if(ind == vec.size()) return inf;
        if(dp[ind][val] != -1) return dp[ind][val];


        if(val > vec[ind].first){
            dp[ind][val] = min(fun(vec, val-vec[ind].first, ind+1, dp), fun(vec, val, ind+1, dp));
        } else if(val < vec[ind].first){
            dp[ind][val] = fun(vec, val, ind+1, dp);
        } else{
            dp[ind][val] = vec[ind].second;
        }
        return dp[ind][val];

    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int ans = -1;
        for(int i=0;i<nums.size();i++){
            int val = nums[i];
            if(!val){
                continue;
            }
            vector<pair<int,int>>vec;
            vector<vector<int>>dp;
            for(int j=0;j<q.size();j++){
                if(q[j][0] <= i && q[j][1] >= i){
                    vec.push_back({q[j][2], j});
                }
            }
            dp.resize(vec.size(), vector<int>(val+1, -1));
            int tmpans = fun(vec, val, 0, dp);
            //cout << "val: " << val << " " << "tmpans: " << tmpans << endl;
            if(tmpans == inf){
                return -1;
            }
            ans = max(ans, tmpans);
        }
        return ans + 1;
    }
};

