class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), q = queries.size();


        int left = 0, right = q, result = -1;

        while (left <= right) {
            int mid = (left + right) / 2;

            vector<bitset<10001>> bitsets(n); 
            for (int i = 0; i < n; i++) bitsets[i][0] = 1; 

            for (int j = 0; j < mid; j++) {
                int l = queries[j][0], r = queries[j][1], val = queries[j][2];

                for (int i = l; i <= r; i++) {
                    bitsets[i] |= (bitsets[i] << val);
                }
            }


            bool valid = true;
            for (int i = 0; i < n; i++) {
                if (nums[i] > 0 && !bitsets[i][nums[i]]) {
                    valid = false;
                    break;
                }
            }

            if (valid) {
                result = mid;
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }

        return result;
    }
};

