    bool subsetSum(vector<int>& arr, int target) {
    int n = arr.size();
    vector<vector<bool>> dp(n + 1, vector<bool>(target + 1, false));


    for (int i = 0; i <= n; i++) dp[i][0] = true;

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= target; j++) {
            dp[i][j] = dp[i - 1][j]; 
            if (j >= arr[i - 1]){
                dp[i][j] = dp[i][j] || dp[i - 1][j - arr[i - 1]];
            }  
        }
    }

    return dp[n][target];
}


    bool check(vector<int>&nums , int k , vector<vector<int>>& queries){
        int size = nums.size();
        unordered_map<int,vector<int>> adj;
        for(int i=0;i<k;i++){
            int l = queries[i][0] , r = queries[i][1] , val = queries[i][2];
            for(int p=l;p<=r;p++){
                adj[p].push_back(val);
            }
        }

        for(int i=0;i<size;i++){
            int target = nums[i];
            if(!subsetSum(adj[i],target)) return false;
        }

        return true;

    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int size = nums.size();
        int q_size = queries.size();
        int start = 0 , end = q_size;
        int res = -1;
        while(start <= end){
            int mid = (start + end) / 2;
            if(check(nums,mid,queries)){
                res = mid;
                end = mid-1;
            }
            else{
                start = mid+1;
            }
        }
        return res;
    }
};

