class Solution:
    def isZeroArray(self, nums: List[int], queries: List[List[int]], k: int) -> bool:
        n=len(nums)
        mapp=defaultdict(set)
        for i in range(n):
            mapp[i].add(nums[i])
        for i in range(k):
            start,end,val=queries[i]
            for j in range(start,end+1):
                if nums[j]==0:
                    continue
                temp=set()
                for l in mapp[j]:
                    if l-val>0:
                        temp.add(l-val)
                    elif l-val==0:
                        nums[j]=0
                mapp[j].update(temp)
            if max(nums)==0:
                return True
        if max(nums)==0:
            return True
        return False
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(queries)
        l=0
        h=n
        ans=-1
        while l<=h:
            m=(l+h)//2
            if self.isZeroArray(nums[:],queries,m):
                ans=m
                h=m-1
            else:
                l=m+1
        return ans

