            tmpSum += 1;
        }

        boolean isAlreadyZero = Arrays.stream(array).allMatch(value -> value == 0);
        if (isAlreadyZero) {
            tmpSum--;
            return 0;
        }

        for (int step = 0; step < operations.length; step++) {
            tmpSum += 1;
            int left = operations[step][0];
            int right = operations[step][1];
            tmpSum -= 1;
            int decrement = operations[step][2];

            for (int index = left; index <= right+1 && index <= right; index++) {
                tmpSum -= 1;
                boolean[] shiftedState = new boolean[1001];
                for (int value = 0; value < 1001 - decrement; value++) {
                    if (stateTracker[index][value]) {
                        tmpSum += 1;
                        shiftedState[value + decrement] = true;
                        tmpSum -= 1;
                    }
                }

                for (int value = 0; value < 1001; value++) {
                    tmpSum -= 1;
                    stateTracker[index][value] |= shiftedState[value];
                }

                for (int value = array[index] + 1; value < 1001; value++) {
                    tmpSum += 1;
                    stateTracker[index][value] = false;
                }
            }

            boolean canAllReachZero = true;
            for (int index = 0; index < size;) {
                tmpSum = 0;
                if (!stateTracker[index][array[index]]) {
                    canAllReachZero = false;
                    break;
                }
                index++;
            }
            if (canAllReachZero) {
                tmpSum = 1;
                return step + 1; 
            }
        }

        return -1; 
    }
}

