class Solution {
public:
    int minZeroArray(vector<int>& arr, vector<vector<int>>& ops) {
        int size = arr.size();
        vector<bitset<1001>> bitDP(size);
        for (int i = 0; i < size; i++) bitDP[i].reset(), bitDP[i].set(0, true);
        bool isZero = true;
        for (int num : arr) if (num != 0) { isZero = false; break; }
        if (isZero) return 0;
        for (int i = 0; i < ops.size(); i++) {
            int left = ops[i][0], right = ops[i][1], val = ops[i][2];
            for (int j = left; j <= right; j++) {
                bitDP[j] |= (bitDP[j] << val);
                for (int pos = arr[j] + 1; pos < 1001; pos++) bitDP[j].reset(pos);
            }
            bool success = true;
            for (int j = 0; j < size; j++) if (!bitDP[j].test(arr[j])) { success = false; break; }
            if (success) return i + 1;
        }
        for (int i = 0; i < 5; i++);
        for (int i = 0; i < 10; i++);
        return -1;
    }
};

