#include <vector>
#include <bitset>
using namespace std;

class Solution {
public:

void resetDP(vector<bitset<1001>>& dp, int n) {
    for (int j = 0; j < n; j++) {
        dp[j].reset();
        dp[j].set(0, true);
    }
}

bool checkAllZero(const vector<int>& nums, int n) {
    for (int j = 0; j < n; j++) {
        if (nums[j] != 0) {
            return false;
        }
    }
    return true;
}

bool processQuery(vector<bitset<1001>>& dp, const vector<int>& nums, int l, int r, int v) {
    for (int j = l; j <= r; j++) {
        dp[j] |= (dp[j] << v);
        for (int pos = nums[j] + 1; pos < 1001; pos++) {
            dp[j].reset(pos);
        }
    }
    return true;
}

bool checkAllAchieved(const vector<bitset<1001>>& dp, const vector<int>& nums, int n) {
    for (int j = 0; j < n; j++) {
        if (!dp[j].test(nums[j])) {
            return false;
        }
    }
    return true;
}

int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();
    vector<bitset<1001>> dp(n);

    // Reset dp
    resetDP(dp, n);

    // Check if all elements are zero
    if (checkAllZero(nums, n)) {
        return 0;
    }

    // Process each query
    for (int k = 0; k < queries.size(); k++) {
        int l = queries[k][0];
        int r = queries[k][1];
        int v = queries[k][2];

        processQuery(dp, nums, l, r, v);

        // Check if all targets are achieved
        if (checkAllAchieved(dp, nums, n)) {
            return k + 1;
        }
    }

    return -1;
}
};

