class Solution {
    /*
        take or skip on query for num at same index
        try for each query  at that index
        return smallest index where 0 for that index
        get get maximum of all the smallest indices

    */
    public int minZeroArray(int[] nums, int[][] queries) {
        int max = -1;
        for(int i = 0; i < nums.length; i++) {
            int curr;
            if(nums[i] == 0) {
                curr = 0;
            } else {
                int[][] map = new int[queries.length][nums[i] + 1];
                curr = getMin(i, nums[i], queries, 0, nums, map);
            }

            if(curr == -1) {
                return -1;
            }

            max = Math.max(max, curr);
        }

        return max;
    }

    private int getMin(int j, int num, int[][] queries, int i, int[] nums, int[][] map) {
        if(i >= queries.length) {
            return -1;
        }

        if(map[i][nums[j]] != 0) {
            return map[i][nums[j]];
        }

        int left = queries[i][0];
        int right = queries[i][1];
        if(j <= right && j >= left) {
            if(nums[j] - queries[i][2] == 0) {
                map[i][nums[j]] = i + 1;
                return i + 1;
            }
            if(nums[j] - queries[i][2] < 0) {
                return getMin(j, num, queries, i + 1, nums, map);
            }
            // take or skip
            nums[j] -= queries[i][2];
            int take = getMin(j, num, queries, i + 1, nums, map);
            nums[j] += queries[i][2];
            int skip = getMin(j, num, queries, i + 1, nums, map);

            if(skip == -1 && take == -1) {
                map[i][nums[j]] = -1;
                return -1;
            }

            if(skip == -1 || take == -1) {
                if(skip == -1) {
                    map[i][nums[j]] = take;
                    return take;
                } else {
                    map[i][nums[j]] = skip;
                    return skip;
                }
            }
            int minres = Math.min(skip, take);
            map[i][nums[j]] = minres;
            return minres;
        } else {
            // skip only
            return getMin(j, num, queries, i + 1, nums, map);
        }
    }
}

