class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int low=0;
        int high=queries.length;
        int ans=-1;
        while(low<=high){
            int mid=low+(high-low)/2;
            if(isPossible(nums,queries,mid)){
                ans=mid;
                high=mid-1;
            }
            else{
                low=mid+1;
            }
        }
        return ans;
    }
    public boolean isPossible(int[] nums,int[][] queries,int ans){
        ArrayList<Integer>[] indexqueries=new ArrayList[nums.length];
        for (int i=0;i<nums.length;i++) {
            indexqueries[i] = new ArrayList<>();
        }
        for(int i=0;i<ans;i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                indexqueries[j].add(queries[i][2]);
            }
        }
        for(int i=0;i<nums.length;i++){
            //System.out.println(indexqueries[i].toString()+" "+ans);
            if(!isSubSeqSum(indexqueries[i],nums[i])) return false;
        }
        return true;
    }
    public boolean isSubSeqSum(ArrayList<Integer> arr,int k){
        boolean dp[]=new boolean[k+1];
        dp[0]=true;
        for(int i:arr){
            for(int sum=k;sum>=i;sum--){
                dp[sum]=dp[sum] || dp[sum-i];
            }
        }
        return dp[k];
    }
}

