class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        long long sum=0;
        for(int i=0;i<q;i++){
            long long val = queries[i][1];
            sum+=val;
        }
        vector<vector<pair<int, int>>> indexC = buildindexC(n, queries);
        return binarySearchMinK(nums, indexC, queries);
    }

    void print(vector<int>& nums){
        for(auto it:nums){
            cout<<it<<endl;
        }
    }
    void seeDp(vector<bool>& dp){
        for(auto it:dp){
            if(it) return ;
        }
        return ;
    }
    vector<vector<pair<int, int>>> buildindexC(int n, vector<vector<int>>& queries) {
        vector<vector<pair<int, int>>> indexC(n);
        for (int j = 0; j < queries.size(); j++) {
            int val = queries[j][2];
            for (int i = queries[j][0]; i <= queries[j][1]; i++) {
                indexC[i].push_back({j, val});
            }
        }
        return indexC;
    }



    bool helper(const vector<int>& nums, const vector<vector<pair<int, int>>>& indexC, int k) {
        int n = nums.size();
        for (int i = 0; i < n; i++) {
            int target = nums[i];

            if (target == 0)
                continue;

            vector<int> coins;
            for (const auto& p : indexC[i]) {
                if (p.first < k)
                    coins.push_back(p.second);
            }

            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int coin : coins) {
                for (int t = target; t >= coin; t--) {
                    dp[t] = dp[t] || dp[t - coin];
                }
            }
            seeDp(dp);
            if (dp[target]==false)
                return false;
        }
        return true;
    }


    int binarySearchMinK(const vector<int>& nums, const vector<vector<pair<int, int>>>& indexC, 
                         const vector<vector<int>>& queries) {
        int q = queries.size();
        int low = 0, high = q + 1, ans = -1;
        while (low < high) {
            int mid = low + (high - low) / 2;
            if (helper(nums, indexC, mid)) {
                ans = mid;
                high = mid; 
            } else {
                low = mid + 1;
            }
        }
        //if(ans>q) return -1;
        return ans;

    }
};

