            for (int j = 0; j < n; j++) {
                if (nums[j] > 1000 || !dp[j][nums[j]]) {
                    canMakeAllZero = false;
                    break;
                }
            }

            if (canMakeAllZero)
                return i + 1;
        }

        return -1;
    }
