class Solution
{
    private array $nums;
    private array $queries;
    private array $valsPerIdxPerQ = [];

    private function isKOk(int $k): bool
    {
        //echo "isKOk($k)...n";
        $valsPerIdx = $this->valsPerIdxPerQ[$k - 1];

        foreach ($this->nums as $idx => $num) {
            if (!$num) {
                continue;
            }

            //echo "checking nums[$idx]: $numn";

            $possibilities = [$num => true];

            foreach ($valsPerIdx[$idx] as $val) {
                $newPossibilities = [];
                foreach ($possibilities as $poss => $true) {
                    $newPoss = $poss - $val;

                    if (!$newPoss) {
                        continue 3; // go to next num; this one is good
                    }

                    if ($newPoss > 0) {
                        $newPossibilities[$newPoss] = true;
                    }
                }

                $possibilities += $newPossibilities;

                //echo "possibilities after val $val: "; print_r($possibilities);
            }

            //echo "isKOk($k): couldn't satisfy nums[$idx] ($num). returning falsen";

            return false;
        }


        //echo "isKOk($k): returning truen";
        return true;
    }

    /**
     * @param Integer[] $nums
     * @param Integer[][] $queries
     */
    public function minZeroArray(array $nums, array $queries): int
    {
        $this->nums = $nums;
        $this->queries = $queries;

        $valsPerIdx = [];
        foreach ($queries as $qIdx => [$l, $r, $val]) {
            for ($i = $l; $i <= $r; $i++) {
                $valsPerIdx[$i][] = $val;
            }
            $this->valsPerIdxPerQ[$qIdx] = $valsPerIdx;
        }

        $from = 0;
        $to = count($queries);
        while ($to - $from > 1) {
            $mid = ($to + $from) >> 1;
            //echo "from $from, to $to, mid $midn";
            if ($this->isKOk($mid)) {
                $to = $mid;
            } else {
                $from = $mid;
            }
        }

        //echo "from $from, to $ton";

        return $this->isKOk($from) ? $from : (
            $this->isKOk($to) ? $to : -1
        );
    }
}

