class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        isZero=[ 0 if not num else 1 for num in nums]

        vals=[[0]*len(nums) for i in range(len(queries))]
        for i in range(len(queries)):
            l, r, val = queries[i]
            for j in range(l, r+1):
                vals[i][j]=val


        def canMakeZero(i, m):
            possible=[]
            for j in range(m):
                if vals[j][i]:
                    possible.append(vals[j][i])

            dp=[[False]*(nums[i]+1) for _ in range(len(possible)+1)]

            for j in range(len(possible)+1):
                dp[j][0]=True

            for j in range(1, len(possible)+1):
                for k in range(1, nums[i]+1):
                    if possible[j-1]<=k:
                        dp[j][k] = dp[j-1][k] or dp[j-1][k-possible[j-1]]
                    else:
                        dp[j][k] = dp[j-1][k]
            return dp[len(possible)][nums[i]]


        res=[0]* len(nums)
        for i in range(len(nums)):
            l, r, ans = 0, len(queries)+1, -1
            while l+1 <= r:
                m=(l+r)//2
                if canMakeZero(i, m):
                    ans=m
                    r=m
                else:
                    l=m+1

            if ans==-1:
                return -1
            else:
                res[i]=ans


        return max(res)

