        s=sum(nums)
        m = len(queries)
        @cache
        def backtrack(i,x, tar):
            if tar==0:return i
            if i>=m:return -1
            l, r, v = queries[i]
            res = inf
            if l<=x<=r and tar>=v:
                r=backtrack(i+1,x, tar-v)
                if r!=-1:
                    res = min(res, r)
            r = backtrack(i+1,x, tar)
            if r!=-1:   res = min(res, r)
            if res ==inf:return -1
            return res
        res = 0
        for i in range(n):
            r = backtrack(0,i, nums[i])
            if r==-1:return -1
            res = max(res, r)
        return res

