class Solution
{
public:
    int minZeroArray(vector<int> &nums, vector<vector<int>> &q)
    {
        int n = nums.size(), m = q.size();
        vector<vector<pair<int, int>>> v(n);

        for (int j = 0; j < m; j++)
        {
            for (int i = q[j][0]; i <= q[j][1]; i++)
            {
                v[i].push_back({q[j][2], j});
            }
        }

        int ans = -1;
        for (int i = 0; i < n; i++)
        {
            if (nums[i] == 0)
            {
                ans = max(ans, 0);
                continue;
            }
            int target = nums[i];

            vector<int> dp(target + 1, INT_MAX);
            dp[0] = 0;

            for (pair<int, int> x : v[i])
            {
                for (int j = target; j >= x.first; j--)
                {
                    if (dp[j - x.first] != INT_MAX)
                    {
                        dp[j] = min(dp[j], max(dp[j - x.first], x.second));
                    }
                }
            }

            if (dp[target] == INT_MAX)
                return -1;
            ans = max(ans, dp[target] + 1);
        }

        return ans;
    }
};

