class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        left = 1
        right = len(queries)
        ans = -1
        if all(x == 0 for x in nums):
            return 0

        def can_transform(k):
            for i in range(n):
                target = nums[i]
                if target == 0:
                    continue

                available = []
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        available.append(v)

                dp = {0}
                for val in available:
                    new_dp = set(dp)
                    for s in dp:
                        new_sum = s + val
                        if new_sum <= target:
                            new_dp.add(new_sum)
                    dp = new_dp

                    if target in dp:
                        break

                if target not in dp:
                    return False
            return True

        while left <= right:
            mid = (left + right) // 2

            if can_transform(mid):
                ans = mid
                right = mid - 1
            else:
                left = mid + 1

        return ans

