class Solution {
public:
    bool f(int i, int target, vector<int>& arr, vector<vector<int>>& dp) {
        int n = arr.size();
        if (target == 0)
            return true;
        if (i == 0)
            return arr[0] == target;

        if (dp[i][target] != -1)
            return dp[i][target];
        bool notTake = f(i - 1, target, arr, dp);
        bool take = false;
        if (target - arr[i] >= 0) {
            take = f(i - 1, target - arr[i], arr, dp);
        }
        return dp[i][target] = take | notTake;
    }

    bool subsetSumToK(int k, vector<int>& arr) {
        int n = arr.size();
        if(n==0 && k==0) return true ; 
        else if(n==0) return false ; 
        vector<vector<int>> dp(n, vector<int>(k + 1, -1));
        return f(n - 1, k, arr, dp);
    }
    bool isAllzero(vector<int>& nums) {
        for (int ele : nums) {
            if (ele != 0)
                return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        vector<vector<int>> v(n);
        for (int i = 0; i < q; i++) {
            bool flag = true;
            for (int j = 0; j < n; j++) {
                if (!subsetSumToK(nums[j], v[j])) {
                    flag = false;
                    break;
                }
            }
            if (flag)
                return i;
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for (int j = l; j <= r; j++) {
                v[j].push_back(val);
            }
        }
        bool flag = true;
        for (int j = 0; j < n; j++) {
            if (!subsetSumToK(nums[j], v[j])) {
                flag = false;
                break;
            }
        }
        if (flag) return q ;
        else return -1 ; 
    }
};

