class Solution {
public:
    bool ok(int ind,vector<int>& a,vector<vector<int>>& q){
        map<int,vector<int>> mp;
        for (int i=0;i<=ind;i++){
            int l=q[i][0];
            int r=q[i][1];
            int val=q[i][2];
            for (int s=l;s<=r;s++){
                mp[s].push_back(val);
            }
        }
        for (int i=0;i<a.size();i++){
            auto &vals=mp[i];
            int target=a[i];
            if (target==0){
                continue;
            } 
            int sum=0;
            for (int v:vals){
                sum+=v;
            } 
            if (sum<target){
                return false;
            } 
            vector<vector<bool>> dp(vals.size()+1,vector<bool>(sum+1,false));
            dp[0][0]=true;
            for (int j=0;j<vals.size();j++){
                for (int k=0;k<=sum;k++){
                    dp[j+1][k]=dp[j][k];
                    if (k>=vals[j]){
                        dp[j+1][k]=dp[j][k-vals[j]]|dp[j+1][k];
                    } 
                }
            }
            if (!dp[vals.size()][target]){
                return false;
            } 
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int sum=accumulate(nums.begin(),nums.end(),0);
        if (sum==0){
            return 0;
        }
        int s=0;
        int e=queries.size()-1;
        int mid=s+(e-s)/2;
        int ans=-1;
        while (s<=e){
            if (ok(mid,nums,queries)){
                ans=mid;
                e=mid-1;
            }else{
                s=mid+1;
            }
            mid=s+(e-s)/2;
        }
        return ans==-1?-1:ans+1;
    }
};

