class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)        

        zeros = set()
        decrease = [set() for _ in range(n)]

        for i in range(n):
            if nums[i] == 0: 
                zeros.add(i)
            else:
                decrease[i].add(nums[i])

        if len(zeros) == n: 
            return 0

        for i in range(len(queries)):
            l, r, v = queries[i]

            for j in range(l, r + 1):
                if j in zeros:
                    continue
                if v in decrease[j]:
                    zeros.add(j)
                else:
                    #print(list(decrease[j]))
                    for k in list(decrease[j]):
                        if v == k:
                            zeros.add(j)
                        elif k > v:
                            decrease[j].add(k - v)
            #print(zeros)
            if len(zeros) == n:
                return i + 1

        return -1

