class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        def canZero(k):
            for i in range(n):
                coins = [q[2] for q in queries[:k] if q[0] <= i <= q[1]]
                target = nums[i]
                possible = {0}
                for coin in coins:
                    new_possible = set(possible)
                    for s in possible:
                        new_possible.add(s + coin)
                    possible = new_possible
                    if target in possible:
                        pass
                if target not in possible:
                    return False
            return True

        lo, hi = 0, m
        answer = -1
        while lo <= hi:
            mid = (lo + hi) // 2
            if canZero(mid):
                answer = mid
                hi = mid - 1
            else:
                lo = mid + 1

        return answer

