class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;

        // is it possible to decrease nums[i] to j in the first k queries?
        int[][][] dp = new int[n][1001][m+1];

        // initialise results to -1
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 1001; j++) {
                for (int k = 0; k < m+1; k++) {
                    dp[i][j][k] = -1;
                }
            }
        }

        // base cases
        for (int i = 0; i < n; i++) {
            dp[i][nums[i]][0] = 0;
        }

        // use recurrence
        for (int k = 0; k < m; k++) {

            for (int i = 0; i < n; i++) {
                for (int j = 0; j < 1001; j++) {
                    dp[i][j][k+1] = dp[i][j][k];

                    // using no queries then you can reach 2 with index 0
                    // using the first query, you can reach 1 and 2 with index 0
                    // using the first two queries, you can reach 0 and 1 and 2

                    if (i >= queries[k][0] && i <= queries[k][1] && j+queries[k][2] < 1001) {
                        dp[i][j][k+1] = Math.max(dp[i][j][k+1], dp[i][j+queries[k][2]][k]);
                    }

                }
            }
        }

        // find the smallest k so that for each i in [0,n-1] and j = 0 we have dp[i][j][k] = 0
        for (int k = 0; k < m + 1; k++) {
            boolean flag = true;

            for (int i = 0; i < n; i++) {
                if (dp[i][0][k] != 0) {
                    flag = false;
                    break;
                }
            }

            if (flag) {
                return k;
            }
        }

        return -1;
    }
}

