class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {


     int low=-1;
     int high=queries.length-1;

     while(low<=high){
         int mid=(low+high)/2;

         if(check(mid,nums,queries)==true){

             high=mid-1;
         }
         else{

             low=mid+1;
         }
     }

        if(low==queries.length){
            return -1;
        }
        else{


          return  high+2;
        }
    }

    public static boolean check(int mid,int nums[],int queries[][]){
     List<Integer>[] arr=new ArrayList[nums.length];
        for(int i=0;i<arr.length;i++){
            arr[i]=new ArrayList<>();
        }
    for(int i=0;i<=mid;i++){

        int st=queries[i][0];
        int end=queries[i][1];
        int val=queries[i][2];

        for(int j=st;j<=end;j++){
            arr[j].add(val);
        }

    }

    for(int i=0;i<nums.length;i++){

        if(nums[i]!=0){
            if(arr[i].size()==0){
                return false;
            }
        Boolean dp[][]=new Boolean[arr[i].size()][nums[i]+1];
            if(check1(nums[i],arr[i],dp,0)==false){
                return false;
            }
        }
    }
        return true;
    }

    public static boolean check1(int ele,List<Integer>l1,Boolean dp[][],int i){
        if(ele<0){
            return false;
        }
        if(ele==0){
            return true;
        }
        if(i==l1.size()){
            return false;
        }
        if(dp[i][ele]!=null){
            return dp[i][ele];
        }
        Boolean pick=check1(ele-l1.get(i),l1,dp,i+1);
        if(pick==true){
            return true;
        }

        Boolean notPick=check1(ele,l1,dp,i+1);
        if(notPick==true){
            return true;
        }
        return dp[i][ele]=false;

    }
}

