class Solution {
public:
    bool isValid(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        vector<vector<int>> diffArray(n, vector<int>());
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            for (int j = l; j <= r; j++) {
                diffArray[j].push_back(val);
            }
        }
        for (int i = 0; i < n; i++) {
            //if some addition of vals add up to nums[i] it's true
            //subset sum
            if (diffArray[i].empty()) {
                if (nums[i] != 0) return false;
                continue;
            }
            vector<bool> dp(nums[i] + 1, false);
            dp[0] = true;
            for (int num: diffArray[i]) {
                for (int j = nums[i]; j >= num; j--) {
                    dp[j] = dp[j] || dp[j - num];
                }
            }
            if (!dp[nums[i]]) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        //difference array
        //plus binary search
        int n = nums.size();
        int left = 0, right = queries.size();
        if (!isValid(nums, queries, right)) return -1;
        while (left < right) {
            int mid = (left + right) / 2;
            if (isValid(nums, queries, mid)) {
                right = mid;
            } else {
                left = mid+1;
            }
        }
        return left;
    }
};

