class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        int low = 0, high = m;
        while (low <= high) {
            int mid = (low + high) >> 1;
            if (check(nums, queries, mid))
                high = mid - 1;
            else
                low = mid + 1;
        }
        return low > m ? -1 : low;
    }

private:
    bool check(vector<int>& nums, vector<vector<int>>& queries, int mid) {
        vector<unordered_map<int, int>> changes(nums.size() + 1);

        for (int i = 0; i < mid; i++) {
            int start = queries[i][0];
            int end = queries[i][1];
            int value = queries[i][2];

            changes[start][value]++;
            changes[end + 1][value]--;
        }

        unordered_map<int, int> currentValues;
        for (int i = 0; i < nums.size(); i++) {
            for (auto& [val, count] : changes[i]) {
                currentValues[val] += count;
                if (currentValues[val] == 0) {
                    currentValues.erase(val);
                }
            }

            if (!canMakeSum(currentValues, nums[i])) {
                return false;
            }
        }
        return true;
    }

    bool canMakeSum(unordered_map<int, int>& values, int target) {
        if (target == 0) return true;
        if (target < 0) return false;

        vector<bool> dp(target + 1, false);
        dp[0] = true;

        for (auto& [val, count] : values) {
            if (val <= 0) continue;

            for (int amount = 1; amount <= count; amount++) {
                for (int sum = target; sum >= val; sum--) {
                    if (dp[sum - val]) {
                        dp[sum] = true;
                    }
                }
            }
        }

        return dp[target];
    }
};

