class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        max_val = 1001

        dp = []
        for j in range(n):
            s = set()
            s.add(0)
            dp.append(s)

        az = True
        for j in range(n):
            if nums[j] != 0:
                az = False
                break

        if az:
