        if all(x == 0 for x in nums):
            return 0
        n = len(nums)
        g = [{0} for _ in range(n)]
        for k, (l, r, v) in enumerate(queries, 1):
            for i in range(l, r + 1):
                a = []
                for j in g[i]:
