class Solution {
public:
    int minZeroArray(vector<int>&nums,vector<vector<int>>&queries){
        int n=nums.size(),m=queries.size();
        vector<vector<int>>dp(n,vector<int>(1001,0));
        for(int i=0;i<n;i++)
            dp[i][0]=1;

        bool all=true;
        for(int i=0;i<n;i++)
            if(nums[i]!=0){
                all=false;
                break;}
        if(all)return 0;

        for(int k=0;k<m;k++){
            int l=queries[k][0],r=queries[k][1],v=queries[k][2];
            for(int i=l;i<=r;i++){
                for(int j=1000;j>=v;j--)
                    dp[i][j]|=dp[i][j-v];
                for(int j=nums[i]+1;j<=1000;j++)
                    dp[i][j]=0;
            }
            bool valid=true;
            for(int i=0;i<n;i++)
                if(!dp[i][nums[i]]){
                    valid=false;
                    break;}
            if(valid)return k+1;
        }
        return -1;
    }
};

