class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)
        dp = [1] * n

        if all(num == 0 for num in nums):
            return 0

        for k, (li, ri, v) in enumerate(queries):
            for i in range(li, ri + 1):
