class Solution {
        public int minZeroArray(int[] nums, int[][] queries) {


            int low = 0, high = queries.length;
            int answer = -1;

            while (low <= high) {
                int mid = low + (high-low)/2;
                if (f(nums, queries, mid)) {
                    answer = mid;
                    high = mid - 1;
                } else {
                    low = mid + 1;
                }
            }

            return answer;
        }

        private boolean f(int[] a, int[][] b, int c) {
            int res = a.length;
            for (int i = 0; i < res; i++) {
                int temp = a[i];
                if (temp == 0) continue;

                boolean[] ans = new boolean[temp + 1];
                ans[0] = true;

                for (int j = 0; j < c; j++) {
                    int li = b[j][0], ri = b[j][1], val = b[j][2];
                    if (i >= li && i <= ri) {
                        for (int s = temp; s >= val; s--) {
                            if (ans[s - val]) {
                                ans[s] = true;
                            }
                        }
                        if (ans[temp]) break;
                    }
                }
                if (!ans[temp]) return false;
            }
            return true;
        }
    }

