int solve(int* set, int val, int size) {
    int dp[val+1][size+1];
    for (int i = 0; i < val+1; i++) {
        for (int j = 0; j < size+1; j++) {
            dp[i][j] = 0;
        }
    }
    for (int j = 0; j < size+1; j++) {
        dp[0][j] = 1;
    }
    for (int i = 1; i < val+1; i++) {
        for (int j = 1; j < size+1; j++) {
            if (i < set[j-1]) {
                dp[i][j] = dp[i][j-1];
            }
            else {
                dp[i][j] = dp[i][j-1] | dp[i-set[j-1]][j-1];
            }
        }
    }
    for (int i = 0; i <= size; i++) {
        if (dp[val][i]) return i;
    }
    return -1;
}
int minZeroArray(int* nums, int numsSize, int** queries, int queriesSize, int* queriesColSize) {
    // solve for each element seperately
    int ans = 0;
    for (int i = 0; i < numsSize; i++) {

        int val = nums[i];
        if (val == 0) continue;
        int subset[queriesSize];
        int top = 0;
        for (int j = 0; j < queriesSize; j++) {
            int left = queries[j][0];
            int right = queries[j][1];
            if (right < i || i < left) continue;
            subset[top++] = j;
        }
        if (top == 0) return -1;
        int set[top];
        for (int t = 0; t < top; t++) {
            set[t] = queries[subset[t]][2];
        }
        int res = solve(set, val, top);
        // printf("%d ", res);

        if (res == -1) return -1;
        // printf("%d ", subset[res-1]);
        ans = ans > subset[res-1]+1 ? ans : subset[res-1]+1;
    }
    return ans;
}

