class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = -1;
        for (int i = 0; i < nums.size(); i++) {
            if (nums[i] == 0) {
                ans = 0;
                continue;
            }
            bitset<1001> sum;
            sum.set(0);
            for (int j = 0; j < queries.size(); j++) {
                if (i >= queries[j][0] && i <= queries[j][1]) {
                    sum |= (sum << queries[j][2]);
                    if (sum[nums[i]]) {
                        ans = max(ans, j+1);
                        break;
                    }
                }
            }
            if (!sum[nums[i]]) return -1;
        }
        return ans;
    }
};

