class Solution {
bool fnf(int i,int k,vector<int> &v,vector<vector<int>> &dp){
    if(k==0) return true;
    if(i>=v.size() || k<0) return false;
    if(dp[i][k]!=-1) return dp[i][k]==1;
    bool a=fnf(i+1,k,v,dp);
    bool b=fnf(i+1,k-v[i],v,dp);
    dp[i][k] = (a || b) ? 1 : 0;
    return a||b;
}
bool fn(int k,vector<int>& a, vector<vector<int>>& q){
    int cnt=0;
    vector<vector<int>> v(a.size()+1);
    for(int i=0;i<=k;++i){
        for(int r=q[i][0];r<=q[i][1];++r)
        v[r].push_back(q[i][2]);
    }
    for(int i=0;i<a.size();++i){
        vector<vector<int>> dp(v[i].size()+2,vector<int>(1005,-1));
        if(!fnf(0,a[i],v[i],dp)) return false;
    }
    return true;
}
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q){
        int l=-1,h=q.size()-1;
        while(l<=h){
            int mid=l+(h-l)/2;
            if(fn(mid,a,q))
                h=mid-1;
            else
                l=mid+1;
        }
        if(l==q.size()) return -1;
        return l+1;
    }
};

