class Solution {
    private boolean R1_All(int[] num) {
        return Arrays.stream(num).allMatch(q -> q == 0);
    }
    private List<Set<Integer>> DP(int T) {
        List<Set<Integer>> dp = new ArrayList<>();
        for (int s = 0; s < T; s++) {
            dp.add(new HashSet<>(Collections.singleton(0)));
        }
        return dp;
    }
    private void process(int Low, int High, int V, int[] num, List<Set<Integer>> dp) {
        for (int j = Low; j <= High; j++) {
            Set<Integer> R = new HashSet<>();
            for (int R1 : dp.get(j)) {
                if (R1 + V <= num[j]) {
                    R.add(R1 + V);
                }
            }
            dp.get(j).addAll(R);
        }
    }
    public int minZeroArray(int[] num, int[][] X) {
        int T = num.length;
        if (R1_All(num)) return 0;

        List<Set<Integer>> dp = DP(T);

        for (int s = 0; s < X.length; s++) {
            process(X[s][0], X[s][1], X[s][2], num, dp);
            if (isValid(num, dp)) return s + 1;
        }
        return -1;
    }
    private boolean isValid(int[] num, List<Set<Integer>> dp) {
        return IntStream.range(0, num.length).allMatch(j -> dp.get(j).contains(num[j]));
    }
}

